class Todo extends Component {
  render() {
    return (
      <div className={ this.props.completed ? 'note completed' : 'note' } >
        <div>
          <input
            onChange={ this.props.updateStatus }
            className="note-toggle"
            type="checkbox"
            checked={this.props.completed}/>
          <label>{this.props.note}</label>
        </div>
        <div className="button" onClick={this.props.deleteTodo}>x</div>
      </div>
    );
  }
}


export default Todo;
